// CSS Blog Topic Generator with Duplicate Prevention
// Randomly selects a category and topic for daily blog post
// Checks against existing posts to avoid duplicates

const categories = {
  "CSS Tips": [
    "CSS Custom Properties for Dynamic Theming",
    "Modern CSS Reset Techniques",
    "CSS Clamp() for Responsive Typography",
    "Using CSS Logical Properties",
    "CSS Container Queries Explained",
    "Advanced CSS Selectors You Should Know",
    "CSS Aspect Ratio Property",
    "CSS Scroll Behavior and Scroll Snap",
    "CSS Custom Properties vs CSS Variables",
    "CSS Cascade Layers Explained"
  ],
  "Animations": [
    "Creating Smooth CSS Hover Effects",
    "CSS Keyframe Animation Best Practices",
    "Transform vs Transition Performance",
    "CSS Animation with View Timeline",
    "Creating Loading Spinners with Pure CSS",
    "Advanced CSS Spring Animations",
    "CSS Motion Path Animation",
    "Parallax Scrolling with CSS",
    "CSS Animation Timing Functions",
    "Creating CSS Micro-interactions"
  ],
  "UI Components": [
    "Modern CSS Button Designs",
    "Custom CSS Toggle Switches",
    "CSS-only Modal Dialogs",
    "Accessible CSS Form Styling",
    "CSS Card Component Variations",
    "Custom CSS Progress Bars",
    "CSS Tooltip Implementations",
    "Modern CSS Navigation Menus",
    "CSS Dropdown Menus",
    "CSS Tab Components"
  ],
  "Game UI": [
    "Recreating Cyberpunk 2077 UI Elements",
    "CSS Health Bars Like RPG Games",
    "Retro Arcade Button Effects",
    "Game Inventory Grid with CSS",
    "Sci-Fi HUD Elements in CSS",
    "Pixel Art Buttons with CSS",
    "Game Menu Transitions",
    "CSS Minimap Components",
    "RPG Status Bar Animations",
    "Retro Game Loading Screens"
  ],
  "Layouts": [
    "CSS Grid vs Flexbox: When to Use What",
    "Modern CSS Layout Patterns",
    "Responsive Design with CSS Grid",
    "CSS Subgrid Explained",
    "Flexbox Alignment Techniques",
    "CSS Multi-column Layouts",
    "Responsive Image Galleries",
    "CSS Masonry Layout",
    "CSS Container Queries for Layouts",
    "Advanced CSS Grid Techniques"
  ]
};

// Get existing posts from the previous node (if available)
let existingTitles = [];
try {
  const existingPosts = $('Check Existing Posts').first().json;
  if (existingPosts && existingPosts.data && Array.isArray(existingPosts.data)) {
    existingTitles = existingPosts.data.map(post => post.title.toLowerCase().trim());
  }
} catch (error) {
  // If we can't get existing posts, continue with empty array
  console.log('Could not fetch existing posts, proceeding without duplicate check');
}

// Get all category names
const categoryNames = Object.keys(categories);

// Try to find a unique topic (max 10 attempts)
let selectedCategory, selectedTopic;
let attempts = 0;
const maxAttempts = 10;

while (attempts < maxAttempts) {
  // Randomly select a category
  selectedCategory = categoryNames[Math.floor(Math.random() * categoryNames.length)];

  // Get topics for the selected category
  const topics = categories[selectedCategory];

  // Randomly select a topic
  selectedTopic = topics[Math.floor(Math.random() * topics.length)];

  // Check if this topic already exists
  if (!existingTitles.includes(selectedTopic.toLowerCase().trim())) {
    break; // Found a unique topic
  }

  attempts++;
}

// If we couldn't find a unique topic after max attempts, use the last selected one
// The API will handle the duplicate check as a fallback

// Generate additional metadata
const today = new Date();
const dateString = today.toLocaleDateString('en-US', { 
  year: 'numeric', 
  month: 'long', 
  day: 'numeric' 
});

// Create tags based on category and topic
const categoryTags = {
  "CSS Tips": "css, tips, techniques, best-practices",
  "Animations": "css, animations, effects, transitions",
  "UI Components": "css, components, ui, design",
  "Game UI": "css, game-ui, gaming, effects",
  "Layouts": "css, layout, grid, flexbox, responsive"
};

const baseTags = categoryTags[selectedCategory] || "css, tutorial";
const topicWords = selectedTopic.toLowerCase().split(' ').filter(word => 
  word.length > 3 && !['css', 'with', 'for', 'and', 'the', 'how', 'to'].includes(word)
);
const additionalTags = topicWords.slice(0, 3).join(', ');
const finalTags = baseTags + (additionalTags ? ', ' + additionalTags : '');

return {
  category: selectedCategory,
  topic: selectedTopic,
  tags: finalTags,
  date: dateString,
  timestamp: today.toISOString(),
  duplicateCheckAttempts: attempts
};

// Content Formatter
// Processes the AI-generated content and formats it for the API

// Get the AI response data - handle different possible structures
const aiResponse = $input.first().json;
const topicData = $('Topic Generator').first().json;

// Extract content from AI response - handle different response formats
let content = '';
if (aiResponse.message && aiResponse.message.content) {
  content = aiResponse.message.content;
} else if (aiResponse.content) {
  content = aiResponse.content;
} else if (typeof aiResponse === 'string') {
  content = aiResponse;
} else {
  // Fallback - try to find content in the response
  content = JSON.stringify(aiResponse);
}

// Clean up the content - remove any markdown formatting
content = content
  .replace(/```html\n?/g, '')
  .replace(/```\n?/g, '')
  .replace(/```css\n?/g, '')
  .replace(/```javascript\n?/g, '')
  .trim();

// Remove any title or category prefixes that might still be there
content = content.replace(/^[^<]*?Tutorial[^<]*?\|[^<]*?Tutorial[^<]*?/, '');
content = content.replace(/^[^<]*?\|[^<]*?Tutorial[^<]*?/, '');
content = content.replace(/^[^<]*?Tutorial[^<]*?/, '');

// Ensure content starts with proper HTML
if (!content.startsWith('<')) {
  // Find the first HTML tag and start from there
  const firstTag = content.match(/<[^>]+>/);
  if (firstTag) {
    content = content.substring(content.indexOf(firstTag[0]));
  }
}

// Extract meta description from the first paragraph
let metaDescription = '';
const firstParagraph = content.match(/<p[^>]*>([^<]+)<\/p>/i);
if (firstParagraph) {
  metaDescription = firstParagraph[1]
    .replace(/<[^>]*>/g, '')
    .substring(0, 155)
    .trim();
  if (metaDescription.length > 150) {
    metaDescription = metaDescription.substring(0, 150).trim() + '...';
  }
}

// Ensure we have a meta description
if (!metaDescription) {
  metaDescription = `Learn ${topicData.topic.toLowerCase()} with this comprehensive CSS tutorial. Includes code examples, best practices, and modern techniques.`;
}

// Create the final blog post data
const blogPost = {
  api_key: "58855f86200ac86ed89742daa0f8d17188d23a89aecfe6332982181e6e6d4541",
  title: topicData.topic,
  content: content,
  category: topicData.category,
  tags: topicData.tags,
  meta_description: metaDescription,
  featured_image: "", // Can be enhanced to generate images
  status: "published",
  author: "CSS Kitsune"
};

return blogPost;

// Response Handler
// Processes the API response and logs the result

const apiResponse = $input.first().json;
const originalData = $('Content Formatter').first().json;

// Check if the API call was successful
if (apiResponse.success) {
  return {
    success: true,
    message: `Blog post "${originalData.title}" created successfully!`,
    post_id: apiResponse.data.id,
    slug: apiResponse.data.slug,
    url: apiResponse.data.url,
    category: originalData.category,
    tags: originalData.tags,
    timestamp: new Date().toISOString()
  };
} else {
  return {
    success: false,
    error: apiResponse.error || 'Unknown error occurred',
    title: originalData.title,
    category: originalData.category,
    timestamp: new Date().toISOString()
  };
}
